	('bc8c60c0-8537-4e70-9a67-36f275078bb1', 'Equity Fundamentals 101', 'A beginner-friendly introduction to equity compensation fundamentals. Learn the basic concepts, terminology, and how startup equity works.', 'beginner', '[{"id": "beginner-module-1", "title": "Introduction to Equity Compensation", "quiz_id": "equity-basics-quiz-1", "content_ids": ["intro-to-equity", "why-companies-offer-equity"], "description": "Understand what equity compensation is and why companies offer it.", "estimated_time": "15 minutes"}, {"id": "beginner-module-2", "title": "Types of Equity: ISOs, NSOs, and RSUs", "quiz_id": "equity-types-quiz", "content_ids": ["equity-types-overview", "iso-vs-nso", "rsu-basics"], "description": "Learn the differences between the main types of equity compensation.", "estimated_time": "20 minutes"}, {"id": "beginner-module-3", "title": "Understanding Your Equity Offer", "content_ids": ["reading-equity-offers", "equity-value-calculation"], "description": "Learn how to read and understand an equity offer in your compensation package.", "estimated_time": "25 minutes", "interactive_id": "offer-value-calculator"}, {"id": "beginner-module-4", "title": "Vesting Schedules Explained", "content_ids": ["vesting-schedules", "cliff-vesting", "vesting-examples"], "description": "Understand how vesting works and what common vesting schedules look like.", "estimated_time": "20 minutes", "interactive_id": "vesting-timeline-calculator"}]', '{"New employees","First-time equity recipients","Career changers entering tech"}', '80-90 minutes', '2025-05-20 02:35:14.434097+00', '2025-05-22 18:06:31.359196+00', NULL, true, true, '{"min_quiz_score": 70, "min_lessons_completed": 6}', NULL, 1),
