	('da869fd5-4791-46e7-a012-76de606508d7', 'Founder Equity: Building and Managing Cap Tables', 'A specialized track for founders on creating equity structures, building cap tables, and managing equity as a company grows.', 'advanced', '[{"id": "founder-module-1", "title": "Founder Equity Structures", "quiz_id": "founder-structures-quiz", "content_ids": ["founder-vesting", "founder-tax-treatment", "co-founder-equity-splits"], "description": "Learn how to structure founding team equity and handle critical early decisions.", "estimated_time": "30 minutes"}, {"id": "founder-module-2", "title": "Building Your Cap Table", "content_ids": ["cap-table-basics", "modeling-funding-rounds", "option-pool-management"], "description": "Create and manage a capitalization table through various funding rounds.", "estimated_time": "40 minutes", "interactive_id": "cap-table-simulator"}, {"id": "founder-module-3", "title": "Equity Compensation Design", "quiz_id": "equity-design-quiz", "content_ids": ["designing-equity-plans", "benchmarking-grants", "employee-communication"], "description": "Design effective equity compensation plans for attracting and retaining talent.", "estimated_time": "35 minutes"}, {"id": "founder-module-4", "title": "Fundraising and Equity Dilution", "content_ids": ["dilution-management", "preferred-vs-common", "negotiating-terms"], "description": "Understand dilution, negotiation tactics, and investor equity structures.", "estimated_time": "35 minutes", "interactive_id": "dilution-calculator"}, {"id": "founder-module-5", "title": "Exit Planning for Founders", "quiz_id": "founder-exit-quiz", "content_ids": ["founder-exit-planning", "acquisition-preparation", "ipo-readiness"], "description": "Strategize for potential exits and maximize founder equity value.", "estimated_time": "35 minutes"}]', '{"Startup founders",Co-founders,"Early startup employees",Entrepreneurs}', '175-190 minutes', '2025-05-20 02:35:14.434097+00', '2025-05-22 18:06:31.359196+00', NULL, true, tru