	('7b0a4736-16a8-4560-b85d-26e926f748f9', 'Evaluating Equity at Early-Stage Companies', 'A practical guide for evaluating equity offers and understanding the risks and rewards of joining an early-stage company.', 'intermediate', '[{"id": "early-stage-module-1", "title": "Evaluating Startup Equity Offers", "content_ids": ["equity-offer-evaluation", "equity-vs-cash-tradeoffs", "negotiation-tactics"], "description": "Learn how to assess and compare equity compensation offers from early-stage companies.", "estimated_time": "30 minutes", "interactive_id": "offer-comparison-tool"}, {"id": "early-stage-module-2", "title": "Valuation and Ownership Calculations", "content_ids": ["409a-valuation-basics", "ownership-calculation", "dilution-modeling"], "description": "Understand how to calculate potential equity value and ownership percentage.", "estimated_time": "25 minutes", "interactive_id": "ownership-calculator"}, {"id": "early-stage-module-3", "title": "Risk Assessment and Company Evaluation", "quiz_id": "risk-assessment-quiz", "content_ids": ["startup-risk-factors", "growth-indicators", "due-diligence-checklist"], "description": "Framework for assessing company potential and the risk profile of your equity.", "estimated_time": "35 minutes"}, {"id": "early-stage-module-4", "title": "Equity and Career Growth", "content_ids": ["refresh-grants", "promotion-equity", "retention-strategies"], "description": "Understand how equity fits into your career progression at startups.", "estimated_time": "25 minutes", "interactive_id": "career-growth-simulator"}]', '{"Job seekers considering startups","Career changers","First-time startup employees"}', '115-130 minutes', '2025-05-20 02:35:14.434097+00', '2025-05-22 18:06:31.359196+00', NULL, true, true, '{"min_quiz_score": 70, "min_lessons_completed": 6}', NULL, 1),
	('e005ec9c-1441-49e3-8ad8-c554d804e903', 'Quick Guide: Exercise Decision Framework', 'A practical framework to help you decide when and how many options to exercise, with a focus on actionable steps.', 'intermediate', '[{"id": "exercise-guide-module-1", "title": "When Should You Exercise?", "content_ids": ["exercise-timing", "company-milestone-indicators", "personal-financial-readiness"], "description": "Key factors to consider when timing your exercise decision.", "estimated_time": "15 minutes", "interactive_id": "exercise-timing-tool"}, {"id": "exercise-guide-module-2", "title": "How Many Options to Exercise", "content_ids": ["partial-vs-full-exercise", "financial-considerations", "dollar-cost-averaging"], "description": "Framework for deciding on exercise quantity based on your financial situation.", "estimated_time": "15 minutes", "interactive_id": "exercise-quantity-calculator"}, {"id": "exercise-guide-module-3", "title": "Exercise Checklist and Process", "quiz_id": "exercise-process-checklist", "content_ids": ["exercise-documentation", "payment-process", "tax-form-preparation"], "description": "Step-by-step guide to the exercise process and documentation.", "estimated_time": "10 minutes"}]', '{"Employees approaching exercise decisions","Those with vesting equity","New option holders"}', '40-45 minutes', '2025-05-20 02:35:14.434097+00', '2025-05-22 18:06:31.359196+00', NULL, true, true, '{"min_quiz_score": 70, "min_lessons_completed": 6}', NULL, 1),
	('6c525887-834d-461b-b346-a2a8b945508e', 'Quick Guide: Equity Tax Filing', 'A concise guide to handling equity-related tax filing requirements during tax season.', 'intermediate', '[{"id": "tax-filing-module-1", "title": "Tax Forms for Equity Compensation", "quiz_id": "tax-forms-quiz", "content_ids": ["form-3921", "form-3922", "w2-equity-reporting", "form-1099-b"], "description": "Understanding the key tax forms related to equity events and how to interpret them.", "estimated_time": "15 minutes"}, {"id": "tax-filing-module-2", "title": "Reporting Equity Transactions", "content_ids": ["schedule-d-reporting", "form-8949", "basis-reporting", "disqualifying-dispositions"], "description": "How to properly report equity exercises, releases, and sales on your tax return.", "estimated_time": "20 minutes", "interactive_id": "tax-reporting-helper"}, {"id": "tax-filing-module-3", "title": "AMT Calculation and Form 6251", "content_ids": ["form-6251-walkthrough", "amt-adjustment-calculation", "amt-credit-form-8801"], "description": "Understanding and completing Form 6251 for Alternative Minimum Tax calculations.", "estimated_time": "15 minutes", "interactive_id": "form-6251-assistant"}]', '{"Equity holders preparing tax returns","People who exercised ISOs","Those who sold equity"}', '50-55 minutes', '2025-05-20 02:35:14.434097+00', '2025-05-22 18:06:31.359196+00', NULL, true, true, '{"min_quiz_score": 70, "min_lessons_completed": 6}', NULL, 1);
